import React from 'react';

const Header = () => {
  return (
    <div className='header'>
      <h1>Hi, my name is <span className='text-[#5C62EC]'>Nikita</span></h1>
      <p className='header__subtitle'>a frontend developer</p>
      <p className='header__desc'>with passion for learning and creating.</p>
      <button className='btn btn-primary mt-10' onClick={handleDownloadClick}>
        Download CV
      </button>
    </div>
  );
}

const handleDownloadClick = () => {
  // Создайте ссылку для скачивания и автоматически нажмите на нее
  const link = document.createElement('a');
  link.href = process.env.PUBLIC_URL + '/СV.pdf'; // Укажите путь к файлу в папке public
  link.target = '_blank'; // Открывать в новой вкладке
  link.download = 'CV.pdf'; // Установите имя файла, которое будет предложено для скачивания
  link.click();
};

export default Header;
