import React from "react";
import { Form, Field } from 'react-final-form';
import styles from "./final_form.module.css";
import btn from "../../stylesheets/btn.module.css";

const onSubmit = (values) => {
  console.log(values);
};

const required = (value) => (value ? undefined : 'Required');
const mustBeNumber = (value) => (isNaN(value) ? 'Must be a number' : undefined);
const composeValidators = (...validators) => (value) =>
  validators.reduce((error, validator) => error || validator(value), undefined);

const FinalForm = () => {
  return (
    <Form
      onSubmit={onSubmit}
      render={({ handleSubmit }) => (
        <form onSubmit={handleSubmit} className={styles.wrapper}>

          <h2>Final Form</h2>
          <div className={styles.section}>
            <Field name="firstName" validate={required}>
              {({ input, meta }) => (
                <div>
                  <input type="text" className={styles.input} {...input} placeholder="Name" />
                  {meta.touched && meta.error && <span className={styles.error}>{meta.error}</span>}
                </div>
              )}
            </Field>

            <Field name="number" validate={composeValidators(required, mustBeNumber)}>
              {({ input, meta }) => (
                <div>
                  <input {...input} className={styles.input}  placeholder="Number" />
                  {meta.touched && meta.error && <span className={styles.error}>{meta.error}</span>}
                </div>
              )}
            </Field>

          </div>

          <button type="submit" className={btn.secondary}>Submit</button>
        </form>
      )}
    />
  );
};

export default FinalForm;
