import React from "react";
import { useForm } from 'react-hook-form';

const Form = () => {
  const { register, handleSubmit, errors } = useForm();

  const onSubmit = (data) => {
    alert(JSON.stringify(data));
  };

  // Устанавливаем общее сообщение об ошибке для правила "required"
  useForm({
    criteriaMode: "all",
  shouldFocusError: true,
  shouldUnregister: true
  })

  return (
    <div className="main__wrapper">
      <h3 className="text-red-500">Form</h3>

      <form className="form" onSubmit={handleSubmit(onSubmit)}>
        <input className="form__input" {...register("firstName", { required: true, maxLength: 20, pattern: /^[A-Za-z]+$/i })} />
        {errors.firstName && <p className="form__error">Поле "Имя" заполнено неверно</p>}

        <input className="form__input" {...register("lastName", { required: true, maxLength: 20, pattern: /^[A-Za-z]+$/i })} />
        {errors.lastName && <p className="form__error">Поле "Фамилия" заполнено неверно</p>}

        <input className="form__input" {...register("age", { required: true, min: 18, max: 99, maxLength: 2 })} />
        {errors.age && <p className="form__error">Поле "Возраст" заполнено неверно</p>}

        <input type="submit" value="Submit" className="btn btn-primary" />
      </form>
    </div>
  );
};

export default Form;
