import React from "react";
import { Form, Field } from 'react-final-form';

const onSubmit = (values) => {
  console.log(values);
};

const validate = (values) => {
  const errors = {};
  const required = value => (value ? undefined : 'Required')
  const mustBeNumber = value => (isNaN(value) ? 'Must be a number' : undefined)
  // const minValue = min => value =>
  //   isNaN(value) || value >= min ? undefined : `Should be greater than ${min}`
  // const composeValidators = (...validators) => value =>
  //   validators.reduce((error, validator) => error || validator(value), undefined)

  if (!values.firstName) {
    errors.firstName = required(values.firstName);
  }

  if (values.number) {
    const numberError = mustBeNumber(values.number);
    if (numberError) {
      errors.number = numberError;
    }
  }

  return errors;
};

const FinalForm = () => {
  return (
    <Form
      onSubmit={onSubmit}
      validate={validate}
      render={({ handleSubmit }) => (
        <form onSubmit={handleSubmit}>
          <h2>Final Form</h2>
          <Field name="firstName">
            {({ input, meta }) => (
              <div>
                <input type="text" {...input} placeholder="First Name" />
                {meta.touched && meta.error && <span>{meta.error}</span>}
              </div>
            )}
          </Field>
          <Field name="number">
            {({ input, meta }) => (
              <div>
                <input type="number" {...input} placeholder="Number" validate={mustBeNumber} />
                {meta.touched && meta.error && <span>{meta.error}</span>}
              </div>
            )}
          </Field>
          <button type="submit">Submit</button>
        </form>
      )}
    />
  );
};

export default FinalForm;
